import React from 'react'
import { Button } from "@chakra-ui/react"

const SMButton = ({ type, children }) => {

    let buttonStyle = {
        shadow: "sm",
        rounded: "lg",
        bg: "white",
        padding: '1rem',
        border: '1px solid #8093BF'
    }

    if(type === 'outlined'){
        return(
            <Button variant="outline" colorScheme="teal" size="xs" sx={buttonStyle} >
                { children }
            </Button>
        )
    }else{
        return(
            <Button colorScheme="teal" size="xs">
                { children }
            </Button>
        )
    }

}

export default SMButton
