import { Circle, Image, SimpleGrid, Text, VStack } from "@chakra-ui/react";
import React from "react";

import * as SMIcons from "../../assets";

const Index = () => {
  let cardStyle = {
    background: "#FDFEFE",
    shadow: "0px 16px 40px rgba(223, 223, 223, 0.8)",
    rounded: "20px",
    padding: "1rem",
  };
  return (
    <SimpleGrid spacing="20" mt="100" w="100%" columns={[1, null, 3]}>
      <VStack mb="10" spacing="5" alignItems="center" sx={cardStyle}>
        <Circle mt="-18" padding="20px 25px" bg="secondary">
          <Image w="30" h="30" src={SMIcons.Experience} alt="Experience" />
        </Circle>
        <Text>2</Text>
        <Text>Years Learnt</Text>
      </VStack>
      <VStack mb="10" spacing="5" alignItems="center" sx={cardStyle}>
        <Text>2</Text>
        <Text>Years Learnt</Text>
      </VStack>
      <VStack mb="10" spacing="5" alignItems="center" sx={cardStyle}>
        <Text>81</Text>
        <Text>Repositiries</Text>
      </VStack>
    </SimpleGrid>
  );
};

export default Index;
