import {
  Flex,
  HStack,
  Image,
  SimpleGrid,
  Text,
  VStack,
} from "@chakra-ui/react";
import React from "react";
import * as SMIcons from "../../assets";

const Index = () => {
  const ProjectCardsRenderer = () => {
    let projectCartStyle = {
      background: "rgba(255, 255, 255, 0.5)",
      boxShadow: "0px 16px 49px rgba(197, 197, 197, 0.25)",
      borderRadius: "15px",
    };
    let projectCartTopBarStyle = {
      display: "flex",
      flexDirection: "row",
      justifyContent: "flex-end",
    };
    return (
      <VStack
        spacing="5"
        p="1.5rem"
        h="100%"
        w="100%"
        maxW="100%"
        justifySelf="center"
        sx={projectCartStyle}
      >
        <Flex w="100%" sx={projectCartTopBarStyle}>
          <Text>Web Application</Text>
        </Flex>
        <HStack w="100%" maxW="100%">
          <Image
            w="50%"
            src="https://res.cloudinary.com/shaun-storage/image/upload/v1628023384/DC449CCC-F237-4FA5-91AC-0EAB36717A80_u88iij.jpg"
          />
          <Image
            w="50%"
            src="https://res.cloudinary.com/shaun-storage/image/upload/v1628023384/76D93D42-56B3-4518-81AC-9AF84E709B40_pvu80c.jpg"
          />
        </HStack>

        <HStack marginTop="20" w="100%" maxW="100%">
          <Text fontSize="30" fontWeight="600">
            SambalSOS
          </Text>
          <Image src={SMIcons.RightArrow} />
        </HStack>
      </VStack>
    );
  };

  return (
    <Flex flexDir="column" alignItems="center" mt="100">
      <Text fontSize="25" fontWeight="bold" color="#606060">
        Notable Projects
      </Text>
      <SimpleGrid spacing="10" mt="100" w="100%" columns={[1, null, 3]}>
        <ProjectCardsRenderer />
        <ProjectCardsRenderer />
        <ProjectCardsRenderer />
      </SimpleGrid>
    </Flex>
  );
};

export default Index;
