import { Flex, Text, VStack } from "@chakra-ui/react";
import { SMButton } from "../../styled/Chakra.custom";
import React from "react";

const Index = () => {
  return (
    <Flex
      flexDir="row"
      justifyContent="center"
      alignItems="center"
      position="relative"
      w="100%"
      h="100%"
      maxH="500px"
    >
      <VStack spacing="5" rounded="full" bg="white" h="300px" w="300px">
        <Text fontSize="3xl" as="h1" fontWeight="700">
          I'm Shaun Mak
        </Text>
        <Text fontSize="lg" fontWeight="200" color="CaptionText" as="h3">
          a Full Stack Developer
        </Text>
        <SMButton>Connect with me</SMButton>
      </VStack>
    </Flex>
  );
};

export default Index;
